def min_changes_to_arithmetic_progression(n, b):
    if n < 2:
        return 0

    def test(x, y):
        if (b[n - 1] + y - (b[0] + x)) % (n - 1) != 0:
            return False
        first = b[0] + x
        last = b[n - 1] + y
        d = (last - first) // (n - 1)
        for i in range(1, n - 1):
            if abs(first + i * d - b[i]) > 1:
                return False
        return True

    def find(x, y):
        res = abs(x) + abs(y)
        first = b[0] + x
        last = b[n - 1] + y
        d = (last - first) // (n - 1)
        for i in range(1, n - 1):
            if abs(first + i * d - b[i]) != 0:
                res += 1
        return res
    ans = float('inf')
    for i in range(-1, 2):
        for j in range(-1, 2):
            if test(i, j):
                ans = min(ans, find(i, j))
    return -1 if ans == float('inf') else ans

