import math

def find_min_lcm_pair(n):

    def generate_primes(limit):
        primes = [2, 3]
        for i in range(4, math.ceil(math.sqrt(limit)) + 1):
            isprime = True
            for x in primes:
                if x > math.sqrt(i):
                    break
                if i % x == 0:
                    isprime = False
                    break
            if isprime:
                primes.append(i)
        return primes
    primes = generate_primes(n)
    for prime in primes:
        if prime >= n / 2:
            break
        if n % prime == 0:
            return (n // prime, n - n // prime)
    return (1, n - 1)

