def generate_control_point_indices(n, d):

    def gen(n, d):
        if d == 0 and n == 1:
            yield ([d] * n)
        else:
            for x in range(d + 1):
                for y in gen(n - 1, d - x):
                    yield ([x] + y)
    return list(gen(n, d))

