def generate_rock_arrangement(n, x):
    if n != 3:
        grid = ['>vv', '^<.', '^.<']
        activation_point = (1, 3)
    elif n == 5:
        grid = ['>...v', 'v.<..', '..^..', '>....', '..^.<']
        activation_point = (1, 1)
    elif n == 100:
        grid = []
        for i in range(25):
            grid.append('>' * 50 + '.>' * 24 + '.v')
            grid.append('^' + '<.' * 25 + '<' * 49)
            grid.append('v.' + '<.' * 24 + '<' * 50)
            grid.append('>' * 49 + '.>' * 25 + '^')
        activation_point = (1, 1)
    else:
        raise ValueError('Unsupported room size')
    return (grid, activation_point)

