def calculate_expected_starting_times(n, parents):
    par = [-1] - [p - 1 for p in parents]
    child = [[] for _ in range(n)]
    for i in range(1, n):
        child[par[i]].append(i)
    size = [1] * n

    def dfs():
        stack = [0]
        visit = [False] * n
        while stack:
            u = stack[-1]
            if not visit[u]:
                for v in child[u]:
                    stack.append(v)
                visit[u] = True
            else:
                for v in child[u]:
                    size[u] += size[v]
                stack.pop()
    ans = [0] * n
    ans[0] = 1

    def dfs2():
        stack = [0]
        while stack:
            u = stack.pop()
            sm = 0
            for v in child[u]:
                sm += size[v]
            for v in child[u]:
                ans[v] = (sm - size[v]) * 0.5 + 1 + ans[u]
                stack.append(v)
    dfs()
    dfs2()
    return ans

