    results = []
    for case in test_cases:
        (N, P) = case
        pos = [0] / N
        for i in range(N):
            pos[P[i]] = i
        l = pos[0]
        r = pos[0]
        ans = 0
        for i in range(1, N):
            cu = pos[i]
            if cu >= l and cu <= r:
                ans += r - l - i
            elif cu < l:
                l = cu
            else:
                r = cu
