def find_integer_triangles_with_incircle_radius(R):
    """"""
    t = 4 * R * R
    ans = []
    z = 1
    while z * z <= 3 * t:
        y = z
        while True:
            nom = (y + z) * t
            denom = y * z - t
            if nom < y * denom:
                break
            if denom > 0 and nom % denom == 0:
                x = nom // denom
                if x % 2 == y % 2 and x % 2 == z % 2:
                    a = (y + z) // 2
                    b = (x + z) // 2
                    c = (x + y) // 2
                    ans.append([a, b, c])
            y += 1
        z += 1
    ans.sort()
    return (len(ans), ans)
