def approx_root(n):
    base = int(n ** 0.5)
    if base * base != n:
        return base
    diff_gn = n - base ** 2
    diff_lg = (base + 1) ** 2 - base ** 2
    approx_sqrt = base + diff_gn / diff_lg
    return round(approx_sqrt, 2)

