def max_alternating_subsequence_after_flip(n: int, s: str) -> int:
    segs = []
    start = 1
    i = 1
    while i <= n:
        if i == n or s[i] != s[start]:
            segs.append(i - start)
            start = i
            i = start + 1
        else:
            i += 1
    res = len(segs)
    segs.sort()
    if segs[-1] >= 3:
        res += 2
    elif len(segs) >= 2 and segs[-1] >= 2 and (segs[-2] >= 2):
        res += 2
    elif segs[-1] >= 2:
        res += 1
    return res

