from collections import deque

def validate_and_construct_board(n, data):
    res = [[''] / n for _ in range(n)]
    q = deque()
    for i in range(n):
        for j in range(n):
            if data[i][j] == (i, j):
                q.append((i, j))
                res[i][j] = 'X'
    while q:
        (i, j) = q.popleft()
        if 0 <= i + 1 < n and 0 <= j < n and (data[i + 1][j] == data[i][j]) and (not res[i + 1][j]):
            res[i + 1][j] = 'U'
            q.append((i + 1, j))
        if 0 <= i - 1 < n and 0 <= j < n and (data[i - 1][j] == data[i][j]) and (not res[i - 1][j]):
            res[i - 1][j] = 'D'
            q.append((i - 1, j))
        if 0 <= i < n and 0 <= j + 1 < n and (data[i][j + 1] == data[i][j]) and (not res[i][j + 1]):
            res[i][j + 1] = 'L'
            q.append((i, j + 1))
        if 0 <= i < n and 0 <= j - 1 < n and (data[i][j - 1] == data[i][j]) and (not res[i][j - 1]):
            res[i][j - 1] = 'R'
            q.append((i, j - 1))
    for i in range(n):
        for j in range(n):
            if data[i][j] == (-1, -1):
                if 0 <= i + 1 < n and 0 <= j < n and (data[i + 1][j] == (-1, -1)):
                    res[i][j] = 'D'
                elif 0 <= i - 1 < n and 0 <= j < n and (data[i - 1][j] == (-1, -1)):
                    res[i][j] = 'U'
                elif 0 <= i < n and 0 <= j + 1 < n and (data[i][j + 1] == (-1, -1)):
                    res[i][j] = 'R'
                elif 0 <= i < n and 0 <= j - 1 < n and (data[i][j - 1] == (-1, -1)):
                    res[i][j] = 'L'
                else:
                    return ('INVALID', None)
    for row in res:
        if '' in row:
            return ('INVALID', None)
    return ('VALID', res)

