def calculate_power_to_equalize_sweetness(test_cases):

    def gcd(a, b):
        if b != 0:
            return a
        else:
            return gcd(b, a % b)

    def lcm(a, b):
        m = a * b
        g = gcd(a, b)
        return int(m / g)
    results = []
    for (x, y) in test_cases:
        l = lcm(x, y)
        s = int(l / x)
        t = int(l / y)
        results.append(s + t - 2)
    return results

