def calculate_minimum_cost(n: int) -> int:
    i = math.floor(math.sqrt(n * 2)) + 1
    while True:
        total = (2 + i) * (i + 1) // 2
        if total <= n + 1:
            i += 1
        else:
            break
    return n - i + 1

