def encode_message(S: str, N: int) -> str:
    s_list = list(S)
    for i in range(2, N, 2):
        (s_list[i], s_list[i - 1]) = (s_list[i - 1], s_list[i])
    for i in range(N):
        m = ord(s_list[i])
        n = ord('a')
        x = m - n
        y = n + 25 - x
        s_list[i] = chr(y)
    return ''.join(s_list)

