def convert_meters_to_metric(meters: int) -> str:
    """"""
    prefixes = ['', 'k', 'M', 'G', 'T', 'P', 'E', 'Z', 'Y']
    count = 0
    while meters >= 1000:
        meters /= 1000.0
        count += 1
    if int(meters) == meters:
        meters = int(meters)
    return f'{meters}{prefixes[count]}m'
