def capitalize_name(full_name: str) -> str:
    """"""
    if not full_name:
        return full_name
    full_name = ' ' + full_name
    capitalized_name = ''
    for i in range(1, len(full_name)):
        if full_name[i - 1] == ' ' and full_name[i] != ' ':
            capitalized_name += full_name[i].upper()
        else:
            capitalized_name += full_name[i]
    return capitalized_name
