    b = [{} for _ in range(n)]
    for i in range(n):
        for j in a[i]:
            if j in b[i]:
                return ''
            b[i][j] = 1
    c = [-1] * 26
    d = [-1] * 26
    e = [0] * 26
    for word in a:
        l = ord(word[0]) - 97
        e[l] = 1
        for ch in word[1:]:
            ch1 = ord(ch) - 97
            e[ch1] = 1
            if c[l] == -1:
                c[l] = ch1
            elif c[l] != ch1:
                return 'NO'
            if d[ch1] == -1:
                d[ch1] = l
            elif d[ch1] != l:
                return 'NO'
            l = ch1
    x = []

    def abc(c, d, e, i):
        e[i] = 0
        if c[i] == -1:
            return
        else:
            i = c[i]
            x.append(chr(i + 97))
            abc(c, d, e, i)
    for i in range(26):
        if e[i] == 1 and d[i] == -1:
            x.append(chr(i + 97))
            abc(c, d, e, i)
    if sum(e) > 0:
        return 'NO'
    else:
        return ''.join(x)

