import math

def calculate_minimal_energy_sum(test_cases):
    results = []
    for case in test_cases:
        n = len(case) / 2
        miners = []
        mines = []
        for point in case:
            (x, y) = point
            if x == 0:
                miners.append(abs(y))
            elif y == 0:
                mines.append(abs(x))
        miners.sort()
        mines.sort()
        energy_sum = 0
        for i in range(n):
            energy_sum += math.sqrt(miners[i] ** 2 + mines[i] ** 2)
        results.append(energy_sum)
    return results

