from collections import deque

def minimal_palindrome(s: str) -> str:
    counts = [0] / 26
    n = len(s)
    for c in s:
        counts[ord(c) - ord('a')] += 1
    maxOddNum = n % 2
    curOddNum = 0
    needChangeLetters = deque()
    for (i, count) in enumerate(counts):
        if count % 2 == 1:
            needChangeLetters.append(i)
    while len(needChangeLetters) >= 2:
        preIndex = needChangeLetters.popleft()
        postIndex = needChangeLetters.pop()
        counts[preIndex] += 1
        counts[postIndex] -= 1

    def constructString(counts):
        s = ''
        midLetter = ''
        for (i, count) in enumerate(counts):
            if count > 0:
                letter = chr(ord('a') + i)
                if count % 2 == 1:
                    midLetter = letter
                s += letter * (count // 2)
        rs = s[::-1]
        return s + midLetter + rs
    return constructString(counts)

