def minimum_operations_to_balance_ears(L, A):

    def zot(x):
        return 0 if x == 0 else (x + 1) % 2 + 1
    D = [1, 0, 0, 0, 0]
    for a in A:
        d0 = D[0] + a
        d1 = min(D[:2]) + [2, 1, 0][zot(a)]
        d2 = min(D[:3]) + [1, 0, 1][zot(a)]
        d3 = min(D[:4]) + [2, 1, 0][zot(a)]
        d4 = min(D) + a
        D = [d0, d1, d2, d3, d4]
    return min(D)

