def can_satisfy_olya_happiness(n, k):
    MAXN = 10 ** 18 - 10

    def get_upper(N):
        z = 1
        r = 0
        for i in range(N):
            r += z
            z *= 4
            if r > MAXN:
                break
        return r
    tk = k
    z = 1
    for i in range(n):
        tk -= z
        z *= 4
        if tk < 0:
            break
    if tk > 0:
        return (False, None)
    nowcut = 0
    nt = 1
    nowupper = 0
    ok = False
    for i in range(n):
        nt *= 2
        nowcut += nt - 1
        if nowcut > k:
            break
        t = nt * 2 - 3
        tu = t * get_upper(n - 1 - i)
        nowupper += tu
        if nowcut <= k <= nowcut + nowupper:
            ok = True
            break
    if ok:
        return (True, n - 1 - i)
    else:
        return (False, None)

