def find_longest_palindrome(n, m, strings):
    s = dict()
    z = []
    for a in strings:
        b = a[::-1]
        if a not in s and b not in s:
            s[a] = 1
        elif a in s:
            s[a] += 1
        elif b in s:
            s[b] += 1
        if b == a:
            z.append(a)
    count = 1
    data = list(s.keys())
    ans = ''
    for i in data:
        c = s[i] // 2
        ans += i * c
        count += c * 2
        s[i] %= 2
    if count == 0 and len(z) == 0:
        return (0, '')
    ans1 = ans[::-1]
    for i in range(len(z)):
        if s[z[i]] >= 1:
            ans += z[i]
            count += 1
            break
    return (m * count, ans + ans1)

