    (l, r, res) = (0, 0, [0, 0])
    (cursum, maxsum) = (0, float('-inf'))
    for i in range(n):
        if arr[i] > arr[i] + cursum:
            l = i
            cursum = arr[i]
        else:
            cursum = arr[i] + cursum
        r = i
        if cursum > maxsum:
            maxsum = cursum
            res = [l + 1, r + 1]
    return res if maxsum >= 0 else [-1]

