    def is_solved(r: list, k: int, m: int) -> int:
        n = len(r)
        for i in range(n - k + 1):
            mx = max(r[i:i + k])
            mc = sum([1 if ri == mx else 0 for ri in r[i:i + k]])
            if mc >= m:
                return None
        return 0

    def solve(r: list, n: int, k: int, m: int) -> int:
        if n == 0:
            return is_solved(r, k, m)
        yr = r.copy()
        yr[n - 1] -= 1
        yes = solve(yr, n - 1, k, m)
        no = solve(r, n - 1, k, m)
        if yes is None:
            return no
        yes += 1
        return yes if no is None else min(yes, no)
    if M == 1:
        return -1
    return solve(R, N, K, M)

