def calculate_blast_sequence(aliens_start, position):

    def move_aliens(aliens, furthest):
        (lst, shoot_path) = ([], [])
        for (x, y, s) in aliens:
            y += s
            if not 0 <= y < N:
                (x, s) = (x + 1, -s)
                y = -y - 1 if y < 0 else 2 * N - y - 1
            (shoot_path if y == Y else lst).append((x, y, s))
            if x > furthest:
                furthest = x
        return (lst, shoot_path, furthest)

    def shoot_target(shoot_path):
        if shoot_path:
            z = max(shoot_path, key=lambda a: (a[0], abs(a[2]), a[2]))
            shoot_path.remove(z)
            shots.append(turn)
    (X, Y) = position
    N = len(aliens_start[0])
    aliens = [(x, y, s) for (x, r) in enumerate(aliens_start) for (y, s) in enumerate(r) if s]
    (shots, furthest, turn) = ([], 0, -1)
    while aliens or furthest < X:
        turn += 1
        (aliens, shoot_path, furthest) = move_aliens(aliens, furthest)
        shoot_target(shoot_path)
        aliens += shoot_path
    return shots if not aliens else None

