def longest_perfect_piece(arr, N):
    a = b = 1
    ans = 1
    for j in range(1, N):
        if abs(arr[j] - arr[b]) == 1:
            if a == b or arr[j] != arr[b - 1]:
                a = b
            b = j
        elif arr[j] != arr[b]:
            a = b = j
        ans = max(ans, j - a + 1)
    return ans

