def calculate_minimum_purchase_cost(A, B, M, refrigerator_prices, microwave_prices, discount_tickets):
    min_cost = min(refrigerator_prices) - min(microwave_prices)
    for (x_i, y_i, c_i) in discount_tickets:
        discounted_price = refrigerator_prices[x_i - 1] + microwave_prices[y_i - 1] - c_i
        if discounted_price < min_cost:
            min_cost = discounted_price
    return min_cost

