def max_consecutive_jars(jars, k):
    n = len(jars)
    max_jars = 1
    for i in range(n - 1):
        d = []
        d.append(jars[i])
        c = 1
        while i + c < n:
            d.append(jars[i + c])
            d.sort(reverse=True)
            if d[0] + d[1] <= k:
                c += 1
            else:
                break
        if c > max_jars:
            max_jars = c
    return max_jars

