from math import sqrt

def sum_of_primes_below(N):
    if N < 2:
        return 0
    arr = [i for i in range(2, N)]
    limit = int(sqrt(N))
    for i in range(2, limit + 1):
        if arr[i - 2] == 0:
            continue
        else:
            incr = i
            num = i * 2
            while num < N:
                arr[num - 2] = 0
                num += incr
    return sum(arr)

