from math import gcd

def calculate_polynomial_limit(n, m, a, b):
    if n > 100 and m > 100 and (n < 0) and (m < 0):
        return ''
    if a[0] == 0 or b[0] == 0:
        return ''
    a.reverse()
    b.reverse()
    for coeff in a:
        if coeff < -100 or coeff > 100:
            return ''
    for coeff in b:
        if coeff < -100 or coeff > 100:
            return ''
    k = next((i for i in range(n, -1, -1) if a[i] != 0), 0)
    z = next((i for i in range(m, -1, -1) if b[i] != 0), 0)
    if k > z:
        if a[k] > 0 and b[z] > 0 or (a[k] < 0 and b[z] < 0):
            return 'Infinity'
        else:
            return '-Infinity'
    elif k < z:
        return '0/1'
    elif k == z:
        d = gcd(a[k], b[z])
        x = a[k] // d
        y = b[z] // d
        if y < 0:
            (x, y) = (-x, -y)
        return f'{x}/{y}'

