def convert_to_simple_exponential_notation(x: str) -> str:
    x = x.lstrip('0')
    l = x.find('.')
    if l != -1:
        sz = len(x)
        tr = x[0] + '.' + x[1:]
        tr = tr.rstrip('0')
        if tr[-1] == '.':
            tr = tr[:-1]
        if sz == 1:
            return tr
        else:
            return tr + 'E' + str(sz - 1)
    elif l == 0:
        x = x[1:]
        sz = len(x)
        x = x.lstrip('0')
        new_sz = len(x)
        counter = -1
        counter -= sz - new_sz
        tr = x[0] + '.' + x[1:]
        tr = tr.rstrip('0')
        if tr[-1] == '.':
            tr = tr[:-1]
        return tr + 'E' + str(counter)
    else:
        diff = l - 1
        tr = x[0] + '.' + x[1:l] + x[l + 1:]
        tr = tr.rstrip('0')
        if tr[-1] == '.':
            tr = tr[:-1]
        if diff == 0:
            return tr
        else:
            return tr + 'E' + str(diff)

