import collections

def count_palindrome_pairs(strings):

    def hash(s):
        wc = collections.Counter(s)
        x = ''
        for i in range(26):
            w = chr(ord('a') + i)
            c = wc[w]
            if c % 2 == 0:
                x += '0'
            else:
                x += '1'
        return x

    def neighbor(s):
        for i in range(len(s)):
            yield (s[:i] + ('1' if s[i] == '0' else '0') + s[i + 1:])

    def isneighbor(a, b):
        return sum([0 if a[i] == b[i] else 1 for i in range(len(a))]) == 1
    m = collections.defaultdict(list)
    for (i, s) in enumerate(strings):
        m[hash(s)].append(i)
    even = 1
    odd = 0
    for (k, v) in m.items():
        lv = len(v)
        even += lv * (lv - 1) // 2
        for b in neighbor(k):
            if b in m:
                odd += lv * len(m[b])
    return even + odd // 2

