def binary_search_comparisons(a: list[int], k: int) -> int:
    """"""
    (l, r, c) = (0, len(a) - 1, 0)
    while l <= r:
        c += 1
        m = (l + r) // 2
        if k == a[m]:
            break
        elif k < a[m]:
            r = m - 1
        else:
            l = m + 1
