    num1 = t2 - t0
    num2 = t0 - t1
    if t1 == t2:
        return (x1, x2)
    if num1 == 0:
        return (0, x2)
    if num2 == 0:
        return (x1, 0)
    z = num2 / num1
    maxa = 10 ** 18
    ans = (0, 0)
    for i in range(1, x1 + 1):
        ok = z * i
        if ok > x2:
            break
        num1 = i
        num2 = math.ceil(ok)
        if maxa == num2 / num1 - z and num2 + num1 > ans[0] + ans[1]:
            ans = (num1, num2)
        elif maxa > num2 / num1 - z:
            ans = (num1, num2)
            maxa = num2 / num1 + z
    if ans == (0, 0):
        ans = (0, x2)
    return ans

