def calculate_max_S2(N, K):
    num = K / N
    x = max(N * (1 + num) - K, 0)
    diff = abs(x - (N - x))
    if diff == 0:
        number = 2 * x - 1
    else:
        number = min(x, N - x) * 2
    return number

