def construct_target_configuration(n, a):
    ans = []
    one = []
    two = []
    three = []
    for i in range(n + 1, -1, -1):
        if a[i] == 1:
            ans.append((i, i))
            one.append(i)
        elif a[i] == 2:
            if len(one) == 0:
                return -1
            curr = one.pop()
            ans.append((curr, i))
            two.append(i)
        elif a[i] == 3:
            if len(two) == 0 and len(one) == 0 and (len(three) == 0):
                return -1
            if len(three) != 0:
                curr = three.pop()
                ans.append((i, i))
                ans.append((i, curr))
            elif len(two) != 0:
                curr = two.pop()
                ans.append((i, i))
                ans.append((i, curr))
            else:
                curr = one.pop()
                ans.append((i, i))
                ans.append((i, curr))
            three.append(i)
    return (len(ans), ans)

