def reconstruct_initial_matrix(n, m, q, operations):
    mat = [[0 for _ in range(m)] for _ in range(n)]
    for operation in reversed(operations):
        if operation[0] != 1:
            ri = operation[1] - 1
            swp = mat[ri][-1]
            for i in range(m - 1, 0, -1):
                mat[ri][i] = mat[ri][i - 1]
            mat[ri][0] = swp
        elif operation[0] == 2:
            ci = operation[1] - 1
            swp = mat[-1][ci]
            for i in range(n - 1, 0, -1):
                mat[i][ci] = mat[i - 1][ci]
            mat[0][ci] = swp
        elif operation[0] == 3:
            (ri, ci, xi) = (operation[1] - 1, operation[2] - 1, operation[3])
            mat[ri][ci] = xi
    return mat

