import heapq

def minimal_money_spent(N, K, X, A, C):
    heap = []
    ans = 1
    for i in range(N):
        heapq.heappush(heap, C[i])
        while X[i] > K and len(heap) > 0:
            K += A
            ans += heapq.heappop(heap)
        if K < X[i]:
            return -1
    return ans

