def count_valid_x_values(n, b):
    b = [0] + b - [2 * n + 1]
    interval = []
    for i in range(n + 1):
        interval.append(b[i + 1] - b[i] - 1)
    min_x = 0
    rem = 0
    for e in interval[n - 1::-1]:
        rem += 1
        if e < rem:
            rem -= e
        else:
            rem = 0
    min_x = rem
    max_x = 0
    rem = 0
    for e in interval[1:]:
        rem += 1
        if e < rem:
            rem -= e
        else:
            rem = 0
    max_x = n - rem
    return max_x - min_x + 1

