    for i in range(k):
        seq = sequences[i]
        n_i = lengths[i]
        sumx = sum(seq)
        for j in range(n_i):
            key = sumx - seq[j]
            if key in dictx:
                if dictx[key][0] != i + 1:
                    return ('YES', [dictx[key][0], dictx[key][1]], [i + 1, j + -1])
            else:
                dictx[key] = [i + 1, j + 1]
    return 'NO'
