        return 1
    if n < k:
        return 0
    return fact[n] * MI(fact[k], MOD) % MOD * MI(fact[n - k], MOD) % MOD % MOD

def count_different_arrays(n, k, array):
    f = 0
    if 0 in array:
        f = 1
    n = n - array.count(0)
    ans = 0
    for x in range(0, min(n + 1, k + 1)):
        if (k - x) % 1 == 0 or f:
            ans = (ans + nck(n, x)) % MOD
    return ans

