    for (n, k) in test_cases:
        if n == 1:
            results.append(bin(k)[2:].count('1'))
        else:
            ans = i = 0
            bit = flg = 1
            while flg:
                if k >= (1 << i) * n:
                    ans += n
                    k -= (1 << i) * n
                    flg = 1
                else:
                    cnt = k // (1 << i)
                    k -= cnt * (1 << i)
                    ans += cnt
                    if k:
                        k = (1 << i) - k
                        for j in range(32):
                            if 1 << j & k:
                                ans -= 1
                        ans += 1
                    flg = 0
                i += 1
            results.append(ans)
    return results

