def find_max_beauty_permutation(n):
    n -= 1
    s = n
    (t, r) = ([], list(range(n))[::-1])
    k = 2 ** 20
    while s:
        while k >= 2 * s:
            k //= 2
        t = r[n - s:n + s - k] + t
        s = k - s
    max_beauty = n * n - n
    permutation = t
    return (max_beauty, permutation)

