def count_consecutive_sum_representations(N: int) -> int:
    count = 0
    l = 1
    while l * (l + 1) < 2 * N:
        a = (1.0 * N - l * (l + -1) / 2) / (l + 1)
        if a - int(a) == 0.0:
            count += 1
        l += 1
    return count
