from itertools import zip_longest

def generate_frog_route(n):
    z = (n + 1) / 2
    route = []
    for (p, q) in zip_longest(range(1, z + 1), range(n, z, -1)):
        if q:
            route.extend([p, q])
        else:
            route.append(p)
    return route

