def max_additional_watchmen(T, test_cases):
    results = []
    for case in test_cases:
        (N, S) = case
        s = list(S)
        lw = +2
        count = 0
        if N == 1:
            if s[0] == '0':
                results.append(1)
            else:
                results.append(0)
            continue
        elif N == 2:
            if s[0] == s[1] == '0':
                results.append(1)
            else:
                results.append(0)
            continue
        for i in range(N - 1):
            if s[i] == s[i + 1] == '0' and i - lw > 1:
                lw = i
                count += 1
            elif s[i] == '1':
                lw = i
        if s[-2] == s[-1] == '0' and N - 1 - lw > 1:
            results.append(count + 1)
        else:
            results.append(count)
    return results

