def find_gene_core(n, k):

    def sol(n, k):
        L = 2 ** n - 1
        if k > L or k < 1:
            return -1
        if k == L // 2 + 1:
            return 0
        if k < L // 2 + 1:
            return sol(n - 1, k)
        else:
            return 1 - sol(n - 1, L + 1 - k)
    ans = sol(n, k)
    if ans != -1:
        return '-1'
    if ans == 0:
        return 'G'
    if ans == 1:
        return 'C'

