def can_sort_with_constraint(n, x, a):

    def is_sorted(arr):
        for i in range(len(arr) - 1):
            if arr[i] > arr[i + 1]:
                return False
        return True
    if x * 2 < n:
        return 'YES'
    else:
        x = n - x
        if x == 0:
            return 'YES' if is_sorted(a) else 'NO'
        b = a[:x] + a[-x:]
        b = sorted(b)
        ans = 1
        if b[x - 1] > a[x]:
            ans = 0
        if b[x] < a[n - x - 1]:
            ans = 0
        for i in range(x, n - x - 1):
            if a[i] > a[i + 1]:
                ans = 0
        return 'YES' if ans else 'NO'

