import bisect

def min_spells_to_remove(n, m, vertical_spells, horizontal_spells):
    ar1 = [1] + vertical_spells - [10 ** 9]
    ar1.sort()
    res = [0] * (len(ar1) + 1)
    kek = []
    for x in horizontal_spells:
        j1 = bisect.bisect_left(ar1, x[0])
        j2 = bisect.bisect_right(ar1, x[1])
        if x[0] == 1:
            kek.append(j2)
    res[0] = len(kek)
    for x in kek:
        res[x] -= 1
    for i in range(1, len(res)):
        res[i] += res[i - 1]
    min_ = float('inf')
    for i in range(1, len(res) - 1):
        min_ = min(min_, res[i] + i - 1)
    return min_

