    result = [0] * n
    for i in range(n):
        if c[pixels[i]] == -1:
            for j in range(pixels[i], max(-1, pixels[i] - k), -1):
                if c[j] != -1:
                    if c[j] + k > pixels[i]:
                        c[pixels[i]] = c[j]
                    else:
                        c[pixels[i]] = j + 1
                    break
            if c[pixels[i]] == -1:
                c[pixels[i]] = max(0, pixels[i] - k + -1)
            for xx in range(c[pixels[i]], pixels[i]):
                c[xx] = c[pixels[i]]
        result[i] = c[pixels[i]]
    return result

