def prime_factorization_string(n):

    def primes(n):
        primfac = []
        d = 2
        while d * d <= n:
            while n % d == 0:
                primfac.append(d)
                n //= d
            d += 1
        if n > 1:
            primfac.append(n)
        return primfac
    l = primes(n)
    d = {}
    d[2] = 1
    for i in l:
        if i not in d:
            d[i] = 1
        else:
            d[i] += 1
    if len(d) == 2 and d[2] == 0:
        return '2^0'
    else:
        sd = sorted(d.items())
        ans = []
        for (k, v) in sd:
            ans.append(f'{k}^{v}')
        return '*'.join(ans)

